import * as React from 'react';
import { getScores } from '../../api/services/scoreService';
import './index.scss';

const LeaderBoard = () => {
  const [dateRange, setDateRange] = React.useState(0);

  React.useEffect(() => {
    getScores();
  }, [])

  const getDateLabel = () => {
    switch (dateRange) {
      case 1:
        return 'This Week';
      case 2:
        return 'This Month';
      case 3:
        return 'All Time';
      case 0:
      default:
        return 'Today';
    }
  }

  return (
    <div className="leaderboard-wrapper">
      <h2>LeaderBoard</h2>
      <div className="leaderboard-body">
        <div>
          <h4>Expert</h4>
        </div>
        <div>
          <h4>Intermediate</h4>
        </div>
        <div>
          <h4>Beginner</h4>
        </div>
      </div>
    </div>
  )
}

export default LeaderBoard;
