import * as React from 'react';
import './index.scss';

const NamePrompt = (props) => {
  const { close, difficulty, timer, visible } = props;
  const [name, setName] = React.useState('');

  const submitScore = () => {
    close();
  }

  return visible && (
    <>
      <div className="backdrop" />
      <div className="prompt-modal">
        <h2>You win!</h2>
        <div>
          <div>It took you {timer} seconds!</div>
          <h4>Please enter your name for the Leader Board:</h4>
          <input onChange={e => setName(e.target.value)} placeholder="Enter name..." />
        </div>
        <a href={`https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fminesweeperonline.com&quote=I%20just%20beat%20Minesweeper%20in%20${timer}%20seconds%20on%20${difficulty}%20difficulty!&hashtag=%23MinesweeperOnline`}>
          Share on Facebook
        </a>
        or
        <button className="interaction-button" disabled={name.length < 1} onClick={() => submitScore}>Save Record</button>
      </div>
    </>
  );
}

export default NamePrompt;
