import * as React from 'react';
import moment from 'moment';
import { getScores } from '../../api/services/scoreService';
import './index.scss';

const LeaderBoard = () => {
  const [dateRange, setDateRange] = React.useState(moment().startOf('day').valueOf());
  const [scoresBeginner, setScoresBeginner] = React.useState([]);
  const [scoresIntermediate, setScoresIntermediate] = React.useState([]);
  const [scoresExpert, setScoresExpert] = React.useState([]);

  React.useEffect(() => {
    getScores(dateRange, 1).then(scores => setScoresBeginner(scores));
    getScores(dateRange, 2).then(scores => setScoresIntermediate(scores));
    getScores(dateRange, 3).then(scores => setScoresExpert(scores));
  }, [dateRange])

  const getDateLabel = () => {
    switch (dateRange) {
      case 1:
        return 'This Week';
      case 2:
        return 'This Month';
      case 3:
        return 'All Time';
      case moment().startOf('day').valueOf():
      default:
        return 'Today';
    }
  }

  return (
    <div className="leaderboard-wrapper">
      <h2>LeaderBoard</h2>
      <div className="leaderboard-body">
        <div>
          <h4>Expert</h4>
        </div>
        <div>
          <h4>Intermediate</h4>
        </div>
        <div>
          <h4>Beginner</h4>
          {scoresBeginner.map((score, idx) => (
            <span>{idx + 1}. ${score.user} -- ${score.time} seconds</span>
          ))}
        </div>
      </div>
    </div>
  )
}

export default LeaderBoard;
