import * as React from 'react';
import './index.scss';

const NamePrompt = (props) => {
  const { close, timer, visible } = props;
  const [name, setName] = React.useState('');


  return visible && (
    <>
      <div className="backdrop" />
      <div className="prompt-modal">
        <h2>You win!</h2>
        <div>
          <div>It took you {timer} seconds!</div>
          <h4>Please enter your name for the Leader Board:</h4>
          <input placeholder="Enter name..." />
        </div>
        <button className="interaction-button" disabled={name.length < 1} onClick={close}>Save Record</button>
      </div>
    </>
  );
}

export default NamePrompt;
