import * as React from 'react';
import GenerateGameBoard from '../../utils/generateGameBoard';

const GameBoard = () => {
  const [boardGrid, setBoardGrid] = React.useState([]);
  const [difficulty, setDifficulty] = React.useState('beginner');

  React.useEffect(() => {
    switch (difficulty) {
      case 'beginner':
      default:
        const newGame = GenerateGameBoard(9, 9, 10);
        setBoardGrid(newGame);
        break;
    }
  }, [difficulty])

  return (
    <div className="board-container">
      {boardGrid.map((gridRow) => (
        <div>
          {gridRow.map((gridColumn) => (
            <div className="board-cell">
              {JSON.stringify(gridColumn.value)}
            </div>
          ))}
        </div>
      ))}
    </div>
  )
}

export default GameBoard;
